            }
        }

        $this->form_fields = array(
            'enabled' => array(
                'title'       => 'Enable/Disable',
                'label'       => 'Enable Stream USDC/SOL Payment Gateway',
                'type'        => 'checkbox',
                'description' => '',
                'default'     => 'no'
            ),
            'title' => array(
                'title'       => 'Title',
                'type'        => 'text',
                'description' => 'This controls the title which the user sees during checkout.',
                'default'     => 'Stream USDC/SOL Payment Gateway',
                'desc_tip'    => true,
            ),
            'description' => array(
                'title'       => 'Description',
                'type'        => 'textarea',
                'description' => 'This controls the description which the user sees during checkout.',
                'default'     => 'Pay with your Phantom and SolFare in USDC/SOL.',
            ),
            'store_address' => array(
                'title'       => 'Store Wallet Address',
                'type'        => 'text',
                'description' => 'public key where store owner receive payment from customers',
                'required'    => true
            ),
            'valid_store_address' => array(
                'type'        => 'hidden',
                'default'     => 'valid',
            ),
            'customer_display_message' => array(
                'type' => 'textarea',
                'default' => '',
                'title' => 'Customer Payment Message',
                'description' => 'This is displayed above the crypto currency payment details on the payment screen (After the customer clicks "Checkout").',
            ),
            'stream_payment_processing_page' => array(
                'type' => 'select',
                'title' => 'Payment processing page',
                'options' => $pages_options,
